    $('.shop-date').text(sslist[0][2])
    $('.shop-genre').text(sslist[0][4])
    $('.shop-price').text(sslist[0][9]+' Euro')
    $('.shop-trailer').attr('src', 'https://www.youtube.com/embed/'+sslist[0][10])

